import { Container } from './episode.styles'

function Episode({ep}) {
    return (
        <Container active={ep}>
            <span style={{color:'#fff',fontSize:'40px',marginTop:'10px'}}>{ep+1}</span>
            <img className='ep__img' src="data:image/png;base64," alt=""/>
            <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eaque omnis, consequuntur blanditiis dolorem tempore fugit </p>
        </Container>
    )
}

export default Episode
